/**
 * Counts the objects in each `nodes` array
 * of a given array of rule results.
 *
 * @param {Array} rules
 * @return {Number}
 * @api public
 */

function countNodes(rules) {
  'use strict';
  var result = 0;
  rules.forEach(function (rule) {
    result += rule.nodes.length || 0;
  });
  return result;
}

/**
 * Expose `countNodes`.
 */

module.exports = countNodes;
