/**
 * Gets an array of the user agents represented
 * in an array of `testResults`.
 *
 * @param {Array} testResults
 * @return {Array}
 * @api public
 */

function getUserAgents(testResults) {
  'use strict';
  return testResults.reduce(function (userAgents, testResult) {
    if (typeof testResult.platform === 'object' && testResult.platform.userAgent &&
    		userAgents.indexOf(testResult.platform.userAgent) < 0) {
      userAgents.push(testResult.platform.userAgent);
    }
  	return userAgents;
  }, []);
}

/**
 * Expose `getUserAgents`.
 */

module.exports = getUserAgents;
