/**
 * Converts common date strings to unixtime.
 * Useful in Array#sort compare functions.
 *
 * @param {String} dateString
 * @return {Number}
 * @api private
 */

function unixTime(dateString) {
  'use strict';
  return new Date(dateString).getTime();
}

/**
 * Comparison function. Sorts by `.endTime`.
 *
 * @param {String} a
 * @param {[String]} b
 * @return {Number}
 * @api public
 */

function sortByTime(a, b) {
  'use strict';
  return unixTime(a.endTime) > unixTime(b.endTime) ? 1 : -1;
}

module.exports = sortByTime;
