
var data = require('../dummydata/with-violations.json');
var countNodes = require('../../reporter/build_html/count-nodes');
var assert = require('chai').assert;

describe('countNodes', function () {

  describe('given a violations array', function () {
    var violations = data.findings.violations;

    it('should return the number of nodes it contains', function () {
      assert.equal(countNodes(violations), 3);
    });
  });
});