
var getUserAgents = require('../../reporter/build_html/get-useragents.js');
var assert = require('chai').assert;

describe('getUserAgent', function () {
  var testResults;

  describe('given an array of testResults', function () {
    before(function () {
      testResults = [
        {platform: { userAgent: 'ie' }},
        {platform: { userAgent: 'ie' }},
        {platform: { userAgent: 'firefox' }}
      ];
    });

    it('should return an array', function () {
      assert(typeof getUserAgents(testResults) === 'object');
      assert(typeof getUserAgents(testResults).push === 'function');
    });

    it('should return the correct number of user agents', function () {
      assert(getUserAgents(testResults).length === 2);
    });
  });
});
