
var data = require('../dummydata/with-violations.json');
var testTotals = require('../../reporter/build_html/test-totals');
var assert = require('chai').assert;

describe('testTotals', function () {
  describe('given an array of checks', function () {
    var checks;

    before(function () {
      checks = [ data, data ];
    });

    it('should return the expected string', function () {
      var expected = '4 violations, involving 6 total nodes';
      assert(testTotals(checks) == expected);
    });
  });
});
