
var sortByTime = require('../../reporter/utils/sort-by-time');
var assert = require('chai').assert;

describe('sortByTime', function () {

  describe('when used as an Array#sort comparitor', function () {
    var checks;

    before(function () {
      checks = [
        {
          '_id': 1,
          'endTime': '2015-01-29T10:08:25.0751405-08:00'
        },
        {
          '_id': 2,
          'endTime': '2015-01-29T10:05:25.0751405-08:00'
        },
        {
          '_id': 3,
          'endTime': '2015-01-29T10:06:25.0751405-08:00'
        },
      ];
    });

    it('should sort an array of checks by time', function () {
      var result = checks.sort(sortByTime);
      assert(result[0]._id == 2);
      assert(result[1]._id == 3);
      assert(result[2]._id == 1);
    });
  });
});
